import React, { Component } from 'react';
import './App.css';
import Header from './components/Header';
import CalculatorView from './components/CalculatorView';
import DataView from './components/DataView';
import BottomNav from './components/BottomNav'
import CalculationModal from './components/CalculationModal';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = this.props.state;
    this.handleClick = this.handleClick.bind(this)
}

  state = {
    calculatorViewVisible: true,
    dataViewVisible: false,
    modalVisible: false
  }

  handleClick() {
    console.log('handleclick triggered');
    this.setState(prevState => ({
      visible: !prevState.visible
    }));
    console.log(this.state);
  }

  render() {
  return (

    <div className="App">

      <Header/>

      <CalculatorView visible={this.state.calculatorViewVisible}/>

      <DataView visible={this.state.dataViewVisible}/>

      <CalculationModal visible={this.state.modalVisible}/>

      <BottomNav onClick={this.handleClick}/>

    </div>
  );
}
}

export default App;
