import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrash } from '@fortawesome/free-solid-svg-icons'
//import db from './DataBase'

class DataView extends Component {
constructor(props) {
    super(props);
    this.state = this.props.state;
}

/*
    componentDidMount() {
        db.table('calculation')
          .toArray()
          .then((calculation) => {

              console.log(calculation[0].id);
              console.log(calculation[0].datetime);
              console.log(calculation[0].onerepmax);
              console.log(calculation[0].goalreps);
              console.log(calculation[0].goalweight);
              console.log(calculation[0].calculatedreps);
              console.log(calculation[0].calculatedweight);
          });
      }
      */

    render() {

    var tableStyle = {
        width: '90%',
        textAlign: 'center',
        justifyContent: 'center',
        margin: '1rem'
    }

    return ( 
    <div>
        {
            this.props.visible
            ?
            <div>

                <ul>
                    {this.props.calcs.map((calc) => 
                    <li                         
                    key={calc.id}>
                    {calc}
                    </li>
                    )}
                    </ul>;

                <h2 style={{textAlign: "center"}}> Data </h2>

                <table style={tableStyle}>
                    <thead>
                        <tr>
                            <th>Date</th>
                            <th>Type</th>
                            <th>One Rep Max Used</th>
                            <th>Result</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>11-26-1984</td>
                            <td>Max Reps</td>
                            <td>20</td>
                            <td>10</td>
                            <td><FontAwesomeIcon icon={faTrash}/></td>
                        </tr>
                        <tr>
                            <td>9-27-1999</td>
                            <td>Max Weight</td>
                            <td>25</td>
                            <td>11</td>
                            <td><FontAwesomeIcon icon={faTrash}/></td>
                        </tr>
                    </tbody>
                </table>
            </div>
            :<div></div>
        }
    </div>
     );
}
}

 
export default DataView;
