import React, { Component } from 'react';


class BottomNav extends Component {
        constructor (props) {
            super(props)
            this.state = {
                calculatorSelected: true,
                dataSelected: false
            }
        }
    clickHandler = () => {
        this.setState({
            calculatorSelected: false,
            dataSelected: true 
        })
    }

    render() { 

        return (
        <div className="container">
            <ul>
                <li><a href="#calculator" id="calculator" onClick={this.clickHandler}>Calculator</a></li>
                <li><a href="#data" id="data" onClick={this.clickHandler}>Data</a></li>
                <div>{this.state.calculatorSelected}</div>
            </ul> 
        </div>
            );
    }
}
 
export default BottomNav;
