import React from 'react';
import './App.css';
import Header from './components/Header';
import CalculatorView from './components/CalculatorView';
import DataView from './components/DataView';
import BottomNav from './components/BottomNav'
import CalculationModal from './components/CalculationModal';


function App() {

  state = {
    calculatorViewVisible: true,
    dataViewVisible: false,
    modalVisible: false
  }

  //constructor and functions here so state from views etc can be handled
  //need something to handle which bottomnav button is selected, then show/hide view
  //need something to handle calculation
  //need something to show/hide modal when calculator button pressed
  //need something to grab values entered in calculator

  return (

    <div className="App">

      <Header/>

      <CalculatorView/> {/*probably some ternary operator stuff to switch between views */}

      <DataView/>

      <CalculationModal/>

      <BottomNav/>

    </div>
  );
}

export default App;
