import React from 'react';

function calculate () {
    var max=document.getElementById("oneRM")
    var weight=document.getElementById("goalWeight")
    var reps=document.getElementById("goalReps")

    if (reps && weight) {
        alert("Enter EITHER goal reps or goal weight, but not BOTH")
    }
    else if (!max && !weight && !reps){
        alert("Enter a one rep max and either goal reps or goal weight")
    }
    else if ((max && !weight) || (max && !reps)){
        alert("Enter goal reps or goal weight")
    }
    else if (!max) {
        alert("Enter a one rep max")
    }
    else if (weight){
        console.log(max.value)
        console.log(reps.value)
        console.log(weight.value)
    }
    else if (reps){
        console.log(max.value)
        console.log(reps.value)
        console.log(weight.value)
    }
    else {alert ("Unknown error during calculation. Try doing 'fahves'.")}


}

const CalculatorView = (props) => {


    var inputStyle = {
        position: 'sticky',
        width: '50%',
        textAlign: 'center',
        justifyContent: 'center',
        height: '10rem',
        marginBottom: '25rem', 
        marginTop: '5rem',
        paddingBottom: '5rem'
        
    }
    return ( 
        <div>
                { props.visible
                ?<div className="container" style={inputStyle}>

                <form>
                    <fieldset>
                        <label for="oneRM">Enter One Rep Max (Lbs)</label>
                        <input type="number" id="oneRM"></input>
                        <p>THEN</p>
                        <label for="goalWeight">Enter Goal Weight</label>
                        <input type="number" id="goalWeight"></input>
                        <p>OR</p>
                        <label for="goalReps">Enter Goal Reps</label>
                        <input type="number" id="goalReps"></input>
                    </fieldset>
                </form>

                <button class="button-large" onClick={calculate}>Calculate</button>

                </div>
                : <div></div>
                }
            
            </div>
     );
}
 
export default CalculatorView;
