import React, { Component } from 'react';
import './App.css';
import Header from './components/Header';
import CalculatorView from './components/CalculatorView';
import DataView from './components/DataView';
import BottomNav from './components/BottomNav'
import CalculationModal from './components/CalculationModal';


class App extends Component {

  constructor(props) {
    super(props);
    this.state = { 
      calculatorViewVisible: true,
      dataViewVisible: false,
      modalVisible: false
     }
     this.handleClick = this.handleClick.bind(this)
  }

  /*
  state = {
    calculatorViewVisible: true,
    dataViewVisible: false,
    modalVisible: false
  }
  */


  handleClick(id) {
    console.log('handleclick triggered');
    console.log("id is " + id.target.id);
    switch (id.target.id) {
      case "calculator":
        console.log("calculator has been selected");
        console.log("id is now "+id.target.id);
        console.log("state is "+this.state)

        this.setState(
          {
            calculatorViewVisible: true,
            dataViewVisible: false,
            modalVisible: false
          }
        )

        break;
      case "data":
        console.log("data has been selected");
        console.log(this)

        this.setState(
          {
            calculatorViewVisible: false,
            dataViewVisible: true,
            modalVisible: false
          }
        )

        break;
      default:
       console.log('default')

      this.setState(
        {
          calculatorViewVisible: true,
          dataViewVisible: false,
          modalVisible: false
        }
      )

    }

  }

  render() {
  return (

    <div className="App">

      <Header/>

      <CalculatorView visible={this.state.calculatorViewVisible}/>

      <DataView visible={this.state.dataViewVisible}/>

      <CalculationModal visible={this.state.modalVisible}/>

      <BottomNav handleClick={this.handleClick}/>

    </div>
  );
}
}

export default App;
