import React, { Component } from 'react';


class BottomNav extends Component {
        constructor (props) {
            super(props)
            this.state = {
                selected: "calculator"
            }
        }
    clickHandler = () => {
        this.setState(state=>({
            selected: "data"
        }));
        console.log(this.state);
    }

    render() { 

        return (
        <div className="container">
            <ul>
                <li><a href="#calculator" id="calculator" onClick={this.clickHandler}>Calculator</a></li>
                <li><a href="#data" id="data" onClick={this.clickHandler}>Data</a></li>
            </ul> 
        </div>
            );
    }
}
 
export default BottomNav;
