import React, { Component } from 'react';


class BottomNav extends Component {

    handleClick(id){
        console.log(id);
     }

    render() { 

        var containerStyle = {
            margin: "0",
            padding: "0",
            position: "fixed",
            width: "100%",
            bottom: "0",
            border: "1px solid #e7e7e7",
            backgroundColor: "#9b4dca",
        }
    
        var ulStyle = {
            listStyleType: "none",
            width: "100%"

        }

        var liStyle = {
            display: "inline",
            width: "50%",
            marginRight: "10%",
            padding: "0",
        }
    
        var aStyle = {
            color: "#fff",
            fontWeight: 'bold',
        }

        return (
        <div className="container" style={containerStyle}>
            <ul style={ulStyle}>
                <li style={liStyle}><a style={aStyle} href="#calculator" id="sample" onClick={() => this.handleClick("calculator")}>CALCULATOR</a></li>
                <li style={liStyle}><a style={aStyle} href="#data" id="sample" onClick={() => this.handleClick("data")}>MY DATA</a></li>
            </ul> 
        </div>
            );
    }
}
 
export default BottomNav;
