import React, { Component } from 'react';
import Rodal from 'rodal';
//import 'rodal/lib/rodal.css';
//import db from './DataBase'


//function saveCalculation () {console.log('calculation saved')}
class CalculationModal extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            visible: false,
            animation: "flip",
            customStyles: {            
                margin: "0",
                marginTop: "5px",
                padding: "0",
                position: "fixed",
                width: "50%",
                bottom: "0",
                backgroundColor: "#9b4dca",
                }
         };
    }

    show() {
      this.setState({ visible: true });
    }
  
    hide() {
      this.setState({ visible: false });
    }

    render() { 

        var modalStyle = {

        }

        return (  
        <div>
            {
                this.state.visible
                ? <div>
                    <Rodal visible={this.state.visible} onClose={this.hide.bind(this)} style={modalStyle}>
                        <div>fucking modal</div>
                    </Rodal>
                </div>
                : <div></div>
            }
        </div> 
        );
    }
}
 
export default CalculationModal;
