import React, { Component } from 'react';


function calculate () {console.log('weight calculated')}

class ReverseMax extends Component {
    constructor(props) {
        super(props);
        this.state = {  }
    }
    render() { 

        var inputStyle = {
            position: 'sticky',
            width: '50%',
            textAlign: 'center',
            justifyContent: 'center',
            height: '10rem',
            marginBottom: '10rem', 
            marginTop: '5rem',
            paddingBottom: '5rem'
            
        }

        return (
            <div className="container" style={inputStyle}>

                <form>
                    <fieldset>
                        <label for="oneRM">Enter One Rep Max (Lbs)</label>
                        <input type="number" placeholder="100" id="oneRM"></input>
                        <label for="goalNumber">Enter Goal Weight or Reps</label>
                        <input type="number" placeholder="10" id="goalNumber"></input>
                        <label for="goalType">Select Goal Type</label>
                        <select id="goalType">
                            <option value="reps">Calculate Max Reps at This Weight</option>
                            <option value="weight">Calculate Max Weight for These Reps</option>
                        </select>
                    </fieldset>
                </form>

                <button class="button" onClick={calculate}>Calculate</button>

            </div>
        );
    }
}
 
export default ReverseMax;
