import React, { Component } from 'react';


function calculate () {console.log('weight calculated')}

class ReverseMax extends Component {
    constructor(props) {
        super(props);
        this.state = {visible: true}
    }
    render() { 

        var inputStyle = {
            position: 'sticky',
            width: '50%',
            textAlign: 'center',
            justifyContent: 'center',
            height: '10rem',
            marginBottom: '25rem', 
            marginTop: '5rem',
            paddingBottom: '5rem'
            
        }

        return (
            <div>
                { this.state.visible
                ?<div className="container" style={inputStyle}>

                <form>
                    <fieldset>
                        <label for="oneRM">Enter One Rep Max (Lbs)</label>
                        <input type="number" placeholder="100" id="oneRM"></input>
                        <label for="goalWeight">Enter Goal Weight</label>
                        <input type="number" placeholder="90" id="goalNumber"></input>
                        <p>OR</p>
                        <label for="goalReps">Enter Goal Reps</label>
                        <input type="number" placeholder="8" id="goalReps"></input>
                    </fieldset>
                </form>

                <button class="button-large" onClick={calculate}>Calculate</button>

                </div>
                : <div></div>
                }
            
            </div>
        );
    }
}
 
export default ReverseMax;
