import React, { Component } from 'react';


class BottomNav extends Component {

    handleClick(id){
        console.log(id);
     }

    render() { 

        var containerStyle = {
            margin: "0",
            padding: "0",
            position: "fixed",
            width: "100%",
            bottom: "0",
            border: "1px solid #e7e7e7",
            backgroundColor: "#9b4dca",
        }
    
        var ulStyle = {
            listStyleType: "none",
            width: "100%",
            textAlign: 'center',
        }

        var li1Style = {
            display: "inline",
            width: "50%",
            paddingRight: "10%",
            borderRight: "1px solid #fff", 
        }

        var li2Style = {
            display: "inline",
            width: "50%",
            paddingLeft: "20%",
            borderLeft: "1px solid #fff"
        }
    
        var aStyle = {
            color: "#fff",
            fontWeight: 'bold',
        }

        return (
        <div className="container" style={containerStyle}>
        <div>
            <ul style={ulStyle}>
                <li style={li1Style}><a style={aStyle} href="#calculator" id="sample" onClick={() => this.handleClick("calculator")}>CALCULATOR</a></li>
                <li style={li2Style}><a style={aStyle} href="#data" id="sample" onClick={() => this.handleClick("data")}>MY DATA</a></li>
            </ul>
        </div> 
        </div>
            );
    }
}
 
export default BottomNav;
