import React, { Component } from 'react';
import './App.css';
import Header from './components/Header';
import CalculatorView from './components/CalculatorView';
import DataView from './components/DataView';
import BottomNav from './components/BottomNav'
import CalculationModal from './components/CalculationModal';


class App extends Component {

  constructor(props) {
    super(props);
    this.state = { 
      calculatorViewVisible: true,
      dataViewVisible: false,
      modalVisible: false,
      result: ''
     }
     this.handleClick = this.handleClick.bind(this)
     this.calculate = this.calculate.bind(this)
     this.show = this.show.bind(this)
  }

  handleClick(id) {
    switch (id.target.id) {
      case "calculator":
        this.setState(
          {
            calculatorViewVisible: true,
            dataViewVisible: false,
            modalVisible: false
          }
        )
        break;
      case "data":
        this.setState(
          {
            calculatorViewVisible: false,
            dataViewVisible: true,
            modalVisible: false
          }
        )
        break;
      default:      
      this.setState(
        {
          calculatorViewVisible: true,
          dataViewVisible: false,
          modalVisible: false
        }
      )

    }
  }


  calculate (){
    var max=document.getElementById("oneRM")
    var weight=document.getElementById("goalWeight")
    var reps=document.getElementById("goalReps")

    if (max.value>0 && weight.value>0 && !reps.value>0) {
        console.log(
            Math.round(((max.value-weight.value)/3)+1)
        );
        this.setState ({result:'1', modalVisible:'true', visible:'true'});
        console.log("result:"+this.state.result);
        console.log("modalVisible/visible:"+this.state.visible)
    }
    else if (max.value>0 && reps.value>0 && !weight.value>0) {
        console.log(
            Math.round(max.value-((reps.value-1)*3))
      )
      this.setState ({result:'2', modalVisible:'true'})
    }
    else {
        alert ("Unable to calculate. Please enter One Rep Max and EITHER Goal Reps or Goal Weight BUT not both")
    };

  }

  show() {
    this.setState({ visible: true });
  }



  render() {
  return (

    <div className="App">

      <Header/>

      <CalculatorView visible={this.state.calculatorViewVisible} calculate={this.calculate} show={this.show}/>

      <DataView visible={this.state.dataViewVisible}/>

      <CalculationModal visible={this.state.modalVisible} result={this.state.result}/>

      <BottomNav handleClick={this.handleClick}/>

    </div>
  );
}
}

export default App;
