import React /*, { Component } */from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrash } from '@fortawesome/free-solid-svg-icons'
import db from './DataBase'
//change this to a class component
//class DataView extends Component {
//constructor(props) {
    //super(props);
    //this.state = this.props.state;
    //render() {
const DataView = (props) => {

    const info = db.table('calculation').then(console.log('done')) //returns promise, not data
    console.log(info)
    const infoarr = Array.from(info)
    console.log(Array.isArray(infoarr)) //true
    console.log(infoarr) //empty

    var tableStyle = {
        width: '90%',
        textAlign: 'center',
        justifyContent: 'center',
        margin: '1rem'
    }

    return ( 
    <div>
        {
            props.visible //this.props.visible
            ?
            <div>


                <h2 style={{textAlign: "center"}}> Data </h2>

                {infoarr.map((calc, index) => (
                <p key={index}>{calc}</p>
                ))}

                <table style={tableStyle}>
                    <thead>
                        <tr>
                            <th>Date</th>
                            <th>Type</th>
                            <th>One Rep Max Used</th>
                            <th>Result</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>11-26-1984</td>
                            <td>Max Reps</td>
                            <td>20</td>
                            <td>10</td>
                            <td><FontAwesomeIcon icon={faTrash}/></td>
                        </tr>
                        <tr>
                            <td>9-27-1999</td>
                            <td>Max Weight</td>
                            <td>25</td>
                            <td>11</td>
                            <td><FontAwesomeIcon icon={faTrash}/></td>
                        </tr>
                    </tbody>
                </table>
            </div>
            :<div></div>
        }
    </div>
     );
}
 
export default DataView;
