import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrash } from '@fortawesome/free-solid-svg-icons'
//import db from './DataBase'

class DataView extends Component {
constructor(props) {
    super(props);
    this.state = this.props.state;
}


    render() {

    var tableStyle = {
        width: '90%',
        textAlign: 'center',
        justifyContent: 'center',
        margin: '1rem'
    }

    return ( 
    <div>
        {
            this.props.visible
            ?
            <div>

                <h2 style={{textAlign: "center"}}> Data </h2>

                <table style={tableStyle}>
                    <thead>
                        <tr>
                            <th>Date</th>
                            <th>One Rep Max Used</th>
                            <th>Result</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>

                         {this.props.calcs.map((calc) => (
                
                        <tr key={calc.id}>
                            <td>{calc.datetime} </td>
                            <td>{calc.onerepmax} </td>
                            <td>
                                {calc.calculatedreps}
                                ? {calc.calculatedreps} <p>reps</p>
                                : {calc.calculatedweight} <p>lbs</p>

                            </td>
                            <td id={calc.id} onClick={this.props.delete}><FontAwesomeIcon icon={faTrash}/></td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
            :<div></div>
        }
    </div>
     );
}
}

 
export default DataView;
