import React, { Component } from 'react';


class Footer extends Component {


    render() { 
        var footerStyle = {
            position: 'sticky',
            width: '100%',
            //textAlign: 'center',
            //justifyContent: 'center',
            marginBottom: '0',  
            //marginTop: '20rem'       
        }

        return (
        <div className="container" style={footerStyle}>

            <div className="row">
                <div className="column"><p>Option 1</p></div>
                <div className="column"><p>Option 2</p></div>
            </div>

        </div>
            );
    }
}
 
export default Footer;
